import './App.css';
import React, {useState} from 'react';

//take input from user
//use the input to call OpenAI

function App() {
  const [movies, setMovies] = useState([
    {title: '', rating: 0},
    {title: '', rating: 0},
    {title: '', rating: 0},
  ])

  const [recommendations, setRecommendations] = useState([])

  const handleInputChange = (index, field, value) => {
    const newMovies = [...movies]
    newMovies[index][field] = value;
    setMovies(newMovies);

  }

  const handleStarClick = (index, starNumber) => {
    handleInputChange(index, 'rating', starNumber);
  }

  const handleSubmit = async () => {
    setRecommendations(["Movie 1", "Movie 2", "Movie 3"])
  }

  return (
    <div className="App">
      <header className="App-header">
        <p>
          Welcome, it is time for movie recommendations!
        </p>

        <form onSubmit={e.preventDefault()}>
          {movies.map((movie, index) => (
            <div key={index}>
              <input placeholder="Movie Title" 
              value={movie.title} onChange={e => handleInputChange}></input>
            </div>
          ))}
        </form>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
