import './App.css';
import React, {useState} from 'react';

//take input from user
//use the input to call OpenAI

function App() {
  const [movies, setMovies] = useState([
    {title: '', rating: 0},
    {title: '', rating: 0},
    {title: '', rating: 0},
  ])

  const [recommendations, setRecommendations] = useState([])
  const [hsaClickedRecommend, setHasClickedRecommend] = useState([false]);


  const handleInputChange = (index, field, value) => {
    const newMovies = [...movies]
    newMovies[index][field] = value;
    setMovies(newMovies);

  }

  const handleStarClick = (index, starNumber) => {
    handleInputChange(index, 'rating', starNumber);
  }   

  const handleSubmit = async () => {
    try {
      const response = await('https://localhost:3001/get-recommendations', {
        method: 'POST',
        headers: {
          'Content=Type': 'application/json'
        }
      })
      if(response.ok){
        const data = await response.json();
        setRecommendations(data.recommendations);
        setHasClickedRecommend(true);
      }
    } catch {

    }

    setRecommendations(["Movie 1", "Movie 2", "Movie 3"])
  };

  return (
    <div className="App">
      <header className="App-header">
        <p>
          Welcome, it is time for movie recommendations!
        </p>

        <form onSubmit={e => e.preventDefault()}>
          {movies.map((movie, index) => (
            <div key={index}>
              <input placeholder="Movie Title" 
              value={movie.title} 
              onChange={e => handleInputChange(index, 'title', e.target.value)} />
              <div>
                {[1, 2, 3, 4, 5].map(starNumber => (
                  <span 
                    key={starNumber}
                    onClick={() => handleStarClick(index, starNumber)}
                    style={{ cursor: 'pointer' }}
                  >
                    {starNumber <= movie.rating ? '★' : '☆'}
                  </span>
                ))}
              </div>
            </div>
          ))}
        </form>
        <button onClick={handleSubmit}>Get Recommendations</button>
        <div>
          <h2>Recommended Movies:</h2>
          <ul>
            {recommendations.map((movie, index) => (
              <li key={index}>{movie}</li>
            ))}
          </ul>
        </div>

      </header>
    </div>
  );
}

export default App;
